import React from 'react';

function AddToCart( { item, action } ) {
    const { id, name } = item;
    const { handleUpdateProductInMyCart } = action;

    let quantity = null;

    const modalSettings = {
        title: "Add To Cart: " + name,
        actionButtons: [{
            label: "Add To Cart",
            attributes: {
                type: "button",
                className: "btn btn-primary",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    handleUpdateProductInMyCart( { item, quantity })
                }
            }
        },{
            label: "Remove",
            attributes: {
                type: "button",
                className: "btn btn-danger",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    handleUpdateProductInMyCart( { id }, 0)
                }
            }
        }],
    }

    const content = (
        <div className="row px-5">
            <div className="col-auto">
                <label className="col-form-label" htmlFor="quantity">Select Quantity</label>
            </div>
            <div className="col-auto">
                <input className="form-control" style={{borderColor: "#B8B8B8"}} type="text" name="quantity" id="quantity"></input>
            </div>
        </div>
    )

    return {
        modalSettings,
        content
    }
}

export default AddToCart;
