import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import Modal from "../Modal";

function ShoesStore(props) {
    const [ isReadOnly, setIsReadOnly ] = useState(true);
    const showActionModal = function () {
        return setIsReadOnly(false)
    }

    const showReadOnlyModal = function () {
        return setIsReadOnly(true)
    }

    return (
        <div>
            <ProductList action={{ showActionModal, showReadOnlyModal }}/>
            <Modal controller={{isReadOnly}}/>
        </div>
    );
}

export default ShoesStore;
