import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import ProductDetail from "./Management/ProductDetail";
import CartDetail from "../Cart/Management/CartDetail";
import Modal from "../Modal";

function ShoesStore() {
    const [ hasActionButton, setHasActionButton ] = useState( false );
    const [ focusItem, setFocusItem ] = useState( null );
    const [ modalContentType, setModalContentType ] = useState( "" );
    const [ modalContent, setModalContent ] =  useState( null );

    const showModal = ( hasActionButton, contentType, focusItem  ) => {
        setModalContentType(contentType);
        setFocusItem( focusItem );
        setHasActionButton(false);
        setModalContent(() => {
            switch (contentType) {
                case "cartDetail" : return ( <CartDetail /> );
                case "shoesItemDetail" : return (<ProductDetail /> );
            }
        });
    }

    return (
        <div>
            <ProductList action={{ showModal }}/>
            <Modal controller={{ hasActionButton, modalContentType, focusItem, modalContent }}/>
        </div>
    );
}

export default ShoesStore;
