import React from 'react';

function AddToCart( { item, action } ) {
    const { id, name, quantity: stock } = item;
    const { handleUpdateProductInMyCart } = action;
    let hasError = false;
    let quantity = 1;

    const modalSettings = {
        title: "Add To Cart: " + name,
        actionButtons: [{
            label: "Add To Cart",
            attributes: {
                type: "button",
                className: "btn btn-primary",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    handleUpdateProductInMyCart( { item, quantity })
                }
            }
        },{
            label: "Remove",
            attributes: {
                type: "button",
                className: "btn btn-danger",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    if (!hasError) {
                        handleUpdateProductInMyCart( { id }, 0)
                    }
                }
            }
        }],
    }

    const content = (
        <div className="px-5 py-3 d-flex flex-column gap-3">
            <div className="row">
                <div className="col-auto">
                    <label className="col-form-label" htmlFor="quantity">Select Quantity</label>
                </div>
                <div className="col-auto">
                    <input className="form-control" style={{borderColor: "#B8B8B8"}}
                        type="text" name="quantity" id="quantity"
                        onChange={
                            () => {
                                let input = document.getElementById("quantity").value;
                                let err = "";
                                switch (true) {
                                    case input > 1 && input < stock && input < 99: quantity = input; break;
                                    case input < 1: err = "Please choose a valid amount"; break;
                                    case input > 99: err = "Please enter a number less than 100"; break;
                                    case input > stock: err = "There is not enough stock"; break;
                                    default: break;
                                }

                                document.getElementById("quantityError").innerText = err;

                                if (err) {
                                    modalSettings.className += "disabled"
                                    hasError = true;
                                    document.getElementById("quantityError").className = "none";
                                } else {
                                    hasError = false;
                                    document.getElementById("quantityError").display = "block";
                                }
                            }
                        }>
                    </input>
                </div>
            </div>
            <div>
                <p id="quantityError" className="form-text" style={{color:"red"}}></p>
            </div>
        </div>
    )

    return {
        modalSettings,
        content
    }
}

export default AddToCart;
