import React from 'react';

function ProductItem({item: shoes}) {
    const { image, name, price } = shoes;

    return (
        <div className="listItem m-auto col-lg-4 col-md-6 p-3">
            <div className="listItem__itemInfo border p-4" >
                <div className="d-flex justify-content-center flex-column">
                    <img src={ image } alt={ name } className="align-self-center mb-3" style={{ height: 300, width: 300 }}/>
                    <div>
                        <h5>{ name }</h5>
                        <p>{ price } $</p>
                    </div>
                </div>
                <div className="listItem__itemAction">
                    <button className="btn btn-dark">
                        <span style={{ marginRight: 10 }}>Add to carts</span>
                        <i className="bi-cart"></i>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProductItem;
