import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import Modal from "../Modal";

function ShoesStore() {
    const [ isReadOnly, setIsReadOnly ] = useState( true );
    const [ focusItemId, setFocusItemId] = useState( null );

    const showModal = () => (isReadOnly) => setIsReadOnly(isReadOnly);
    const showActionModal = function (itemId) {
        showModal(false)
        setFocusItemId(itemId)
    }

    const showReadOnlyModal = function (itemId) {
        setFocusItemId(itemId)
        setIsReadOnly(true)
    }

    return (
        <div>
            <ProductList action={{ showActionModal, showReadOnlyModal }}/>
            <Modal controller={{isReadOnly, focusItemId}}/>
        </div>
    );
}

export default ShoesStore;
