import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import Modal from "../Modal";

function ShoesStore(props) {
    const { isReadOnly, setIsReadOnly } = useState();
    const showActionModal = () => {
        setIsReadOnly(false)
        console.log(isReadOnly)
    }

    const showReadOnlyModal = () => {
        return setIsReadOnly(true)
    }

    return (
        <div>
            <ProductList showActionModal={showActionModal} showReadOnlyModal={showReadOnlyModal}/>
            <Modal isReadOnly={isReadOnly}/>
        </div>
    );
}

export default ShoesStore;
