import React, {useEffect, useState} from 'react';
import ProductList from "./Product/ProductList";
import ProductDetail from "./Product/ProductDetail";
import AddToCart from "./Product/AddToCart";
import Modal from "../Modal";

function ShoesStore() {
    const [ modalContent, setModalContent ] =  useState( null );
    const [ modalSettings, setModalSettings ] =  useState( {} );
    const [ selectedItem, setSelectedItem ] =  useState( {} );
    const [ selectedQuantity, setSelectedQuantity ] =  useState( {} );
    const [ myCart, setMyCart ] = useState(JSON.parse(localStorage.getItem("myCart")) || []);



    const handleShowModal = ( contentType, focusItem ) => {
        if (!focusItem) return null;
        const { id, name } = focusItem;
        setSelectedItem(focusItem);
        setModalContent(() => {
            switch (contentType) {
                case "AddToCart" :
                    setModalSettings({
                        title: "Add To Cart: " + name,
                        actionButtons: [{
                            label: "Add To Cart",
                            attributes: {
                                type: "button",
                                className: "btn btn-primary",
                                "data-bs-dismiss": "modal",
                                onClick: () => {
                                    handleUpdateProductInMyCart()
                                }
                            }
                        },{
                            label: "Remove",
                            attributes: {
                                type: "button",
                                className: "btn btn-danger",
                                "data-bs-dismiss": "modal",
                                onClick: () => {
                                    handleUpdateProductInMyCart( { id }, 0)
                                }
                            }
                        }],
                    })
                    return ( <AddToCart item={ focusItem } /> );

                case "ProductDetail" :
                    setModalSettings({
                        title: name,
                        className: "modal-lg"
                    })
                    return (<ProductDetail  item={ focusItem } /> );
                default: return "";
            }
        });
    }

    const handleGetQuantity = (quantity) => {
        setSelectedQuantity(quantity)
    }

    const handleCloseModal = () => {
        setModalContent(null);
        setModalSettings({});
    }

    const handleUpdateProductInMyCart = (quantity) => {
        const { id } = selectedItem;
        const cart = [...myCart];
        let itemCartIndex = cart.findIndex((element) => element.id === id);
        let target = {
            id,
            quantity
        }

        switch (true) {
            case itemCartIndex > -1 && quantity > 0: cart[itemCartIndex] = target; break;
            case itemCartIndex > -1 && quantity <= 0 : cart.splice(itemCartIndex, 1); break;
            case itemCartIndex <= -1 && quantity > 0 : cart.push(target); break;
            default: break;
        }

        setMyCart(cart);
        handleCloseModal();
    }

    useEffect(() => {
        localStorage.setItem("myCart", JSON.stringify(myCart));
    }, [myCart])

    return (
        <div className="shoesStore">
            <ProductList action={{ handleShowModal, handleUpdateProductInMyCart }}/>
            <Modal props={{ modalSettings, modalContent }} action={{ handleCloseModal }}/>
        </div>
    );
}

export default ShoesStore;
