import React from 'react';

function ProductDetail( { item } ) {
    const { image, quantity, ...newItem} = item;

    return (
        <div className="d-flex gap-5 align-items-center">
            <div>
                <img src={image} alt={newItem.description} width="350" height="200px"
                     style={{objectFit: "cover", objectPosition: "center", transform: "translateY(20)"}}/>
            </div>
            <div>
                <p><span className="fw-bold">Id:</span> {item.id}</p>
                <p><span className="fw-bold">Name:</span> {item.name}</p>
                <p><span className="fw-bold">Alias:</span> {item.alias}</p>
                <p><span className="fw-bold">Price:</span> {item.price}</p>
                <p><span className="fw-bold">About:</span> {item.shortDescription}</p>
                <p><span className="fw-bold">Detail:</span> {item.description}</p>
            </div>
        </div>
    );
}

export default ProductDetail;
