import React from 'react';

function MyCart( { myCart } ) {
    const modalSettings = {
        title: "My Cart",
        className: "modal-lg",
        dialogClassName: "modal-dialog-scrollable"
    }

    return {
        modalSettings,
        content: (
            <div className="mx-5">
                {
                    myCart.map( (item, index) => {
                        const { id, name, image, price, quantity, totalPrice } = item;
                        return (
                            <div key={id} className={
                                (index !== 0) ? "py-3" : "pb-3"
                            } style={
                                (index !== 0) ? {borderTop: "1px solid #9D9D9D"} : null
                            }>
                                <div className="productDetail d-flex gap-5
                                                justify-content-center align-items-center flex-row">
                                    <div className="productDetail_image">
                                        <img src={image} alt={name} width="100px" height="100px"
                                             style={{objectFit: "cover", objectPosition: "center",
                                                 transform: "translateY(15px)"}}/>
                                    </div>
                                    <div className="productDetail_infoTable w-75">
                                        <table className="w-100">
                                            <tbody>
                                                <tr>
                                                    <td className="px-2 py-1 fw-bold">Name:</td>
                                                    <td className="px-2 py-1" colSpan={3}>{ name }</td>
                                                </tr>
                                                <tr>
                                                    <td className="px-2 py-1 fw-bold">Quantity:</td>
                                                    <td className="px-2 py-1">{ quantity }</td>
                                                    <td className="px-2 py-1 fw-bold">Price:</td>
                                                    <td className="px-2 py-1"
                                                        style={{textAlign: "right"}}>{ price } $</td>
                                                </tr>
                                                <tr>
                                                    <td className="px-2 py-1" colSpan={2}></td>
                                                    <td className="px-2 py-1 fw-bold">Total Price:</td>
                                                    <td className="px-2 py-1"
                                                        style={{textAlign: "right"}}>{ totalPrice } $</td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}


export default MyCart;
