import React from 'react';
function ProductItem({ item: shoes, action } ) {
    const { id, image, name, price } = shoes;
    const { handleShowModal, handleUpdateProductInMyCart } = action;

    const productDetailModalSettings = {
        title: name,
        className: "modal-lg"
    }

    const addToCartModalSettings = {
        title: "Add To Cart: " + name,
        actionButtons: [{
            label: "Add To Cart",
            attributes: {
                type: "button",
                className: "btn btn-primary",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    handleUpdateProductInMyCart( { id }, 1)
                }
            }
        },{
            label: "Remove From Cart",
            attributes: {
                type: "button",
                className: "btn btn-delete",
                "data-bs-dismiss": "modal",
                onClick: () => {
                    handleUpdateProductInMyCart( { id }, 1)
                }
            }
        }],
    }

    return (
        <div className="listItem m-auto col-lg-4 col-md-6 p-3">
            <div className="border p-4" >
                <div className="listItem__itemCard d-flex justify-content-center flex-column"
                     onClick={() => handleShowModal( productDetailModalSettings, "ProductDetail",  shoes )}
                     data-bs-toggle="modal" data-bs-target="#baseModal">
                    <img src={ image } alt={ name } className="itemCard__image align-self-center mb-3"
                         style={{ height: 280, width: 280 }}/>
                    <div>
                        <h5 className="itemCard__title">{ name }</h5>
                        <p className="itemCard__price">{ price } $</p>
                    </div>
                </div>
                <div className="listItem__itemAction">
                    <button className="btn btn-dark"
                            onClick={() => handleShowModal( addToCartModalSettings, "AddToCart", shoes )}
                            data-bs-toggle="modal" data-bs-target="#baseModal">
                        <span style={{ marginRight: 10 }}>Add to carts</span>
                        <i className="bi-cart"></i>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProductItem;
