import React, {useEffect, useState} from 'react';
import ProductList from "./Product/ProductList";
import ProductDetail from "./Product/ProductDetail";
import AddToCart from "./Cart/AddToCart";
import Modal from "../Modal";

function ShoesStore() {
    const [ modalContent, setModalContent ] =  useState( null );
    const [ modalSettings, setModalSettings ] =  useState( {} );
    const [ myCart, setMyCart ] = useState(JSON.parse(localStorage.getItem("myCart")) || []);

    const handleShowModal = ( modalSettings, contentType, focusItem ) => {
        setModalSettings(modalSettings);
        setModalContent(() => {
            switch (contentType) {
                case "AddToCart" :
                    return ( <AddToCart item={ focusItem } action={ handleUpdateProductInMyCart } /> );
                case "ProductDetail" :
                    return (<ProductDetail  item={ focusItem } /> );
                default: return "";
            }
        });
    }

    const handleCloseModal = () => {
        setModalContent(null);
        setModalSettings({});
    }

    const handleUpdateProductInMyCart = (item, quantity) => {
        const { id } = item;
        const cart = [...myCart];
        let itemCartIndex = cart.findIndex((element) => element.id === id);
        let myQuantity = {
            id,
            quantity
        }

        if (itemCartIndex > -1) { cart[itemCartIndex] = myQuantity } else { cart.push(myQuantity) };
        
        setMyCart(cart);
    }

    useEffect(() => {
        localStorage.setItem("myCart", JSON.stringify(myCart));
    }, [myCart])

    return (
        <div className="shoesStore">
            <ProductList action={{ handleShowModal }}/>
            <Modal props={{ modalSettings, modalContent }} action={{ handleCloseModal }}/>
        </div>
    );
}

export default ShoesStore;
