import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import Modal from "../Modal";

function ShoesStore() {
    const [ hasActionButton, setHasActionButton ] = useState( false );
    const [ focusItem, setFocusItem ] = useState( null );
    const [ modalContentType, setModalContentType ] = useState( "" )

    const showModal = ( hasActionButton, contentType, focusItem  ) => {
        setModalContentType(contentType);
        setFocusItem( focusItem );
        setHasActionButton(false);
    }

    return (
        <div>
            <ProductList action={{ showModal }}/>
            <Modal controller={{ hasActionButton, modalContentType, focusItem }}/>
        </div>
    );
}

export default ShoesStore;
