import React, {useState} from 'react';
import ProductList from "./Management/ProductList";
import ProductDetail from "./Management/ProductDetail";
import AddToCart from "../Cart/Management/AddToCart";
import Modal from "../Modal";

function ShoesStore() {
    const [ hasActionButton, setHasActionButton ] = useState( false );
    const [ modalContent, setModalContent ] =  useState( null );
    const [ modalClass, setModalClass ] =  useState( "" );

    const showModal = ( hasActionButton, contentType, focusItem, modalClass  ) => {
        setModalClass( modalClass )
        setHasActionButton( hasActionButton );
        setModalContent(() => {
            switch (contentType) {
                case "AddToCart" : return ( <AddToCart item={ focusItem } /> );
                case "ProductDetail" : return (<ProductDetail  item={ focusItem } /> );
            }
        });
    }

    return (
        <div>
            <ProductList action={{ showModal }}/>
            <Modal controller={{ hasActionButton, modalContent }}/>
        </div>
    );
}

export default ShoesStore;
