import React from 'react';

function MyCart( { myCart } ) {
    const modalSettings = {
        title: "My Cart"
    }

    return {
        modalSettings,
        content: (
            <div className="mx-3">
                {
                    myCart.map( (item) => {
                        const { id, name, image, price, quantity, totalPrice } = item;
                        return (
                            <div key={id} className="productDetail d-flex gap-4 align-items-center flex-column flex-lg-row">
                                <div className="productDetail_image">
                                    <img src={image} alt={name} width="100px" height="100px"
                                         style={{objectFit: "cover", objectPosition: "center", transform: "translateY(15px)"}}/>
                                </div>
                                <div className="productDetail_infoTable">
                                    <table>
                                        <tbody>
                                        <tr>
                                            <td className="px-2 py-1 fw-bold">Name:</td>
                                            <td className="px-2 py-1" colSpan={3}>{ name }</td>
                                        </tr>
                                        <tr>
                                            <td className="px-2 py-1 fw-bold">Quantity:</td>
                                            <td className="px-2 py-1">{ quantity }</td>
                                            <td className="px-2 py-1 fw-bold">Price:</td>
                                            <td className="px-2 py-1" style={{textAlign: "right"}}>{ price } $</td>
                                        </tr>
                                        <tr>
                                            <td className="px-2 py-1 fw-bold">Total Price:</td>
                                            <td className="px-2 py-1" style={{textAlign: "right"}}>{ totalPrice } $</td>
                                        </tr>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}


export default MyCart;
