import React from 'react';
import {calculateNewValue} from "@testing-library/user-event/dist/utils";

function ProductDetail( { item } ) {
    const { image, quantity, ...newItem} = item;
    const { id, name, alias, price, shortDescription, description} = newItem;

    return (
        <div className="d-flex gap-5 align-items-center">
            <div>
                <img src={image} alt={newItem.description} width="350" height="200px"
                     style={{objectFit: "cover", objectPosition: "center", transform: "translateY(15px)"}}/>
            </div>
            <div>
                <p><span className="fw-bold">Id:</span> { id }</p>
                <p><span className="fw-bold">Name:</span> { name }</p>
                <p><span className="fw-bold">Alias:</span> { alias }</p>
                <p><span className="fw-bold">Price:</span> { price }</p>
                <p><span className="fw-bold">About:</span> { shortDescription }</p>
                <p><span className="fw-bold">Detail:</span> <br /> { description }</p>
            </div>
        </div>
    );
}

export default ProductDetail;
