import React from 'react';

function MyCart( { myCart } ) {
    console.log(myCart)
    const modalSettings = {
        title: "My Cart",
        className: "modal-lg"
    }

    return {
        modalSettings,
        content: (
            <div>
                {
                    myCart.map(item => {
                        const { id, name, image, price, quantity, totalPrice } = item;
                        return (
                            <div key={id} className="productDetail d-flex gap-3 align-items-center flex-column flex-lg-row">
                                <div className="productDetail_image">
                                    <img src={image} alt={name} width="100px" height="100px"
                                         style={{objectFit: "cover", objectPosition: "center", transform: "translateY(15px)"}}/>
                                </div>
                                <div className="productDetail_infoTable">
                                    <table>
                                        <tbody>
                                        <tr style={{borderBottom:"1px solid #9D9D9D"}}>
                                            <td className="px-2 py-3 fw-bold">Name</td>
                                            <td className="px-2 py-3">{ name }</td>
                                        </tr>
                                        <tr style={{borderBottom:"1px solid #9D9D9D"}}>
                                            <td className="px-2 py-3 fw-bold">Price</td>
                                            <td className="px-2 py-3">{ price }</td>
                                        </tr>
                                        <tr style={{borderBottom:"1px solid #9D9D9D"}}>
                                            <td className="px-2 py-3 fw-bold">Quantity</td>
                                            <td className="px-2 py-3">{ quantity }</td>
                                        </tr>
                                        <tr style={{borderBottom:"1px solid #9D9D9D"}}>
                                            <td className="px-2 py-3 fw-bold">Total Price</td>
                                            <td className="px-2 py-3">{ totalPrice }</td>
                                        </tr>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}


export default MyCart;
