import React, {useEffect, useState} from 'react';
import ProductList from "./Product/ProductList";
import ProductDetail from "./Product/ProductDetail";
import AddToCart from "./Cart/AddToCart";
import Modal from "../Modal";

function ShoesStore() {
    const [ showModal, setShowModal ] =  useState( false );
    const [ modalContent, setModalContent ] =  useState( null );
    const [ modalSettings, setModalSettings ] =  useState( {} );
    const handleShowModal = ( modalSettings, contentType, focusItem ) => {
        setModalSettings(modalSettings);
        setModalContent(() => {
            switch (contentType) {
                case "AddToCart" : return ( <AddToCart item={ focusItem } /> );
                case "ProductDetail" : return (<ProductDetail  item={ focusItem } /> );
                default: return "";
            }
        });

        return (<Modal props={{ modalSettings, modalContent }}/>)
    }

    const [ myCart, setMyCart ] = useState(JSON.parse(localStorage.getItem("myCart")) || []);
    useEffect(() => {
        localStorage.setItem("myCart", JSON.stringify(myCart));
    }, [myCart])
    const updateProductInMyCart = (item) => {
        let cart = [...myCart];
        let itemCartIndex = cart.findIndex((element) => element.id === item.id)

        if (itemCartIndex > -1) { cart[itemCartIndex] = item } else { myCart.push(item) }
        
        setMyCart(myCart);
    }

    return (
        <div className="shoesStore">
            <ProductList action={{ handleShowModal, updateProductInMyCart }}/>
        </div>
    );
}

export default ShoesStore;
