import React from 'react';

function ProductItem({item: shoes}) {
    const { image, name, price } = shoes;
    return (
        <div className="m-auto col-lg-4 col-md-6 p-3">
            <div className="border d-flex justify-content-center flex-column p-4"  style={{ height: 500 }} >
                <img src={ image } alt={ name } className="align-self-center" style={{ height: 300, width: 300 }}/>
                <div>
                    <h5>{ name }</h5>
                    <p>{ price } $</p>
                </div>
            </div>
            <div>
                <button className="btn btn-dark"></button>
            </div>
        </div>
    );
}

export default ProductItem;
