import React from 'react';
function Modal({props}) {
    const { show, modalSettings: settings, modalContent: content } = props;
    const { actionButtons, title, htmlClass} = settings;
    console.log(actionButtons);
    return (
        <>
            {(!show) ? <></> :
                <div className={ "modal fade " + htmlClass } id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel"
                     aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">{ title }</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                { content }
                            </div>
                            <div className="modal-footer justify-content-center">
                                <button type="button" className="btn btn-outline-secondary"
                                        data-bs-dismiss="modal">Close</button>
                                {
                                    ( actionButtons.length > 0 ) ?
                                        actionButtons.map( btn => ( <button {...btn.attributes}>{btn.label}</button> )) : null
                                }
                            </div>
                        </div>
                    </div>
                </div>
            }
        </>
    );
}

export default Modal;
