import React from 'react';
function Modal({controller}) {
    const { hasActionButton, modalContentType: contentType, focusItem: item } = controller;

    const rendered = () => {
        console.log(contentType)
        switch (contentType) {
            case "cartDetail" : return (
                <div>Cart</div>
            )
            case "shoesItemDetail" : return (
                <div>Item</div>
            )
        }
    }

    return (
        <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel"
             aria-hidden="true">
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                        <pre>{JSON.stringify(item, null, 4)}</pre>
                        {rendered()}
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                        {(hasActionButton) ?(<button type="button" className="btn btn-primary">Save changes</button>) : null}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Modal;
